examples.code:
$( document.body )
  .click(function() {
    $( this ).append( $( "<div>" ) );
    var n = $( "div" ).size();
    $( "span" ).text( "There are " + n + " divs. Click to add more." );
  })

  // Trigger the click to start
  .click();

examples.css:
body {
  cursor: pointer;
  min-height: 100px;
}
div {
  width: 50px;
  height: 30px;
  margin: 5px;
  float: left;
  background: blue;
}
span {
  color: red;
}
