examples.code:
function aClick() {
  $( "div" ).show().fadeOut( "slow" );
}
$( "#bind" ).click(function() {
  $( "#theone" )
    .bind( "click", aClick )
    .text( "Can Click!" );
});
$( "#unbind" ).click(function() {
  $( "#theone" )
    .unbind( "click", aClick )
    .text( "Does nothing..." );
});

examples.css:
button {
  margin: 5px;
}
button#theone {
  color: red;
  background: yellow;
}

examples.code:
var foo = function() {
  // Code to handle some kind of event
};

$( "p" ).bind( "click", foo ); // ... Now foo will be called when paragraphs are clicked ...

$( "p" ).unbind( "click", foo ); // ... foo will no longer be called.
